import React from 'react';

const ScheduleNavbar = ({ isOpen, toggleSidebar, schedule }) => {
  return (
    <>
      {/* Hamburger Button */}
      <button
        onClick={toggleSidebar}
        className={`fixed top-4 right-4 p-2 rounded ${
          isOpen
            ? 'bg-clippersRed text-white'
            : 'bg-transparent border border-white text-white'
        } transition duration-200`}
      >
        <svg
          xmlns="http://www.w3.org/2000/svg"
          fill="none"
          viewBox="0 0 24 24"
          strokeWidth={2}
          stroke="currentColor"
          className="w-6 h-6"
        >
          <path
            strokeLinecap="round"
            strokeLinejoin="round"
            d="M4 6h16M4 12h16M4 18h16"
          />
        </svg>
      </button>

      {/* Sidebar */}
      <div
        className={`fixed top-0 right-0 h-full w-64 bg-clippersBlue text-clippersWhite p-4 transition-transform duration-300 ${
          isOpen ? 'transform translate-x-0' : 'transform translate-x-full'
        }`}
      >
        <h2 className="text-xl font-bold mb-4 mt-12">Upcoming Games</h2>
        <ul>
          {schedule.map((game, index) => (
            <li key={index} className="mb-2">
              {game.date} - {game.opponent} at {game.time}
            </li>
          ))}
        </ul>
      </div>
    </>
  );
};

export default ScheduleNavbar;
