  {name: "P.J. Tucker", image: "https://cdn.nba.com/headshots/nba/latest/1040x760/200782.png", "position": "Forward", athlete_staff: "athlete"},
  {name: "Kobe Brown", image: "https://cdn.nba.com/headshots/nba/latest/1040x760/1631308.png",  "position": "Forward", athlete_staff: "athlete"},
  {name: "Kai Jones", image: "https://cdn.nba.com/headshots/nba/latest/1040x760/1630545.png",  "position": "Center-Forward", athlete_staff: "athlete"},
  {name: "Norman Powell", image: "https://cdn.nba.com/headshots/nba/latest/1040x760/1626181.png", athlete_staff: "athlete"},
  {name: "Nicolas Batum", image: "https://cdn.nba.com/headshots/nba/latest/1040x760/201587.png", athlete_staff: "athlete"},
  {name: "Ivica Zubac", image: "https://cdn.nba.com/headshots/nba/latest/1040x760/1627826.png", athlete_staff: "athlete"},
  {name: "Derrick Jones Jr.", image: "https://cdn.nba.com/headshots/nba/latest/1040x760/1627884.png", athlete_staff: "staff"},
  {name: "Kevin Porter Jr.", image: "https://cdn.nba.com/headshots/nba/latest/1040x760/1629645.png", athlete_staff: "staff"},
  {name: "Tyronn Lue (Head Coach)", image: "https://cdn.nba.com/headshots/coaches/latest/1040x760/lue_tyronn.png", athlete_staff: "staff"},
  {name: "Jeff Van Gundy (Assistant Coach)", image: "https://cdn.nba.com/headshots/coaches/latest/1040x760/van_gundy_jeff.png", athlete_staff: "staff"},
  {name: "Armond Hill (Assistant Coach)", image: "https://cdn.nba.com/headshots/coaches/latest/1040x760/hill_armond.png", athlete_staff: "staff"},
  {name: "Kevin Eastman (Assistant Coach)", image: "https://cdn.nba.com/headshots/coaches/latest/1040x760/eastman_kevin.png", athlete_staff: "staff"},
  {name: "Rex Kalamian (Assistant Coach)", image: "https://cdn.nba.com/headshots/coaches/latest/1040x760/kalamian_rex.png", athlete_staff: "staff"},
  {name: "Jason Powell (Head Athletic Trainer)", image: "https://cdn.nba.com/headshots/trainers/latest/1040x760/powell_jason.png", athlete_staff: "staff"},
  {name: "Jasen Powell (Assistant Athletic Trainer)", image: "https://cdn.nba.com/headshots/trainers/latest/1040x760/powell_jasen.png", athlete_staff: "staff"}
]



const Team = () => {
  const [filter, setFilter] = useState("athlete");
  const [filteredPlayers, setFilteredPlayers] = useState([]);
  const [selectedPlayer, setSelectedPlayer] = useState(null);

  // Update filtered players whenever the filter changes
  useEffect(() => {
    const updatedFilteredPlayers = players.filter(
      (player) => player.athlete_staff === filter
    );
    setFilteredPlayers(updatedFilteredPlayers);

    // Set the default selected player to the first player in the updated list
    setSelectedPlayer(updatedFilteredPlayers[0] || null);
  }, [filter]);

  return (
    <div className="p-8">
      <h1 className="text-2xl font-bold mb-4">LA Clippers Team</h1>

      {/* Athlete/Staff Filter */}
      <div className="mb-6">
        <label htmlFor="filter-select" className="block mb-2 text-lg font-medium">
          Filter by:
        </label>
        <select
          id="filter-select"
          className="border p-2 rounded-md w-full max-w-md"
          value={filter}
          onChange={(e) => setFilter(e.target.value)}
        >
          <option value="athlete">Athletes</option>
          <option value="staff">Staff</option>
        </select>
      </div>

      {/* Dropdown for Filtered Players */}
      <div className="mb-6">
        <label htmlFor="player-select" className="block mb-2 text-lg font-medium">
          Select a Player:
        </label>
        <select
          id="player-select"
          className="border p-2 rounded-md w-full max-w-md"
          value={selectedPlayer?.name || ""}
          onChange={(e) =>
            setSelectedPlayer(
              filteredPlayers.find((p) => p.name === e.target.value)
            )
          }
        >
          {filteredPlayers.map((player) => (
            <option key={player.name} value={player.name}>
              {player.name} - {player.position}
            </option>
          ))}
        </select>
      </div>

      {/* Player/Staff Image and Details */}
      {selectedPlayer && (
        <div className="flex flex-col items-center">
          <h2 className="text-xl font-semibold mb-4">{selectedPlayer.name}</h2>
          <p className="text-lg text-gray-600 mb-4">{selectedPlayer.position}</p>
          <img
            src={selectedPlayer.image}
            alt={selectedPlayer.name}
            className="w-48 h-48 object-cover rounded-full shadow-lg"
          />
        </div>
      )}
    </div>
  );
};

export default Team;
