import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const Navbar = () => {
  const [collapsed, setCollapsed] = useState(false);

  return (
    <nav
      className={`${
        collapsed ? 'w-20' : 'w-64'
      } bg-clippersBlue text-clippersWhite h-screen p-4 flex flex-col items-center fixed top-0 left-0 transition-all duration-300`}
    >
      {/* Collapse/Expand Button */}
      <button
        onClick={() => setCollapsed(!collapsed)}
        className="text-clippersWhite bg-clippersRed p-2 rounded mb-6"
      >
        {collapsed ? (
          <svg
            xmlns="http://www.w3.org/2000/svg"
            fill="none"
            viewBox="0 0 24 24"v
            strokeWidth={2}
            stroke="currentColor"
            className="w-6 h-6"
          >
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              d="M4 6h16M4 12h16M4 18h16"
            />
          </svg>
        ) : (
          <svg
            xmlns="http://www.w3.org/2000/svg"
            fill="none"
            viewBox="0 0 24 24"
            strokeWidth={2}
            stroke="currentColor"
            className="w-6 h-6"
          >
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              d="M4 6h16M4 12h16M4 18h16"
            />
          </svg>
        )}
      </button>

      {/* Navigation Links (Visible only when expanded) */}
      {!collapsed && (
        <>
          <img
            src="https://cdn.nba.com/logos/nba/1610612746/primary/L/logo.svg"
            alt="LA Clippers Logo"
            className="w-24 h-auto mb-8"
          />
          <div className="space-y-4 text-center">
            <Link to="/" className="block hover:underline">
              Home
            </Link>
            <Link to="/team" className="block hover:underline">
              Team
            </Link>
            <Link to="/league" className="block hover:underline">
              League
            </Link>
          </div>
        </>
      )}
    </nav>
  );
};

export default Navbar;
