import React, { useState } from 'react';
import { Link } from 'react-router-dom';

const Navbar = () => {
  const [collapsed, setCollapsed] = useState(false);

  return (
    <nav
      className={`${
        collapsed ? 'w-20' : 'w-64'
      } bg-clippersBlue text-clippersWhite h-screen p-4 flex flex-col items-center fixed top-0 left-0 transition-all duration-300`}
    >
      {/* Collapse/Expand Button */}
      <button
        onClick={() => setCollapsed(!collapsed)}
        className="text-clippersWhite bg-clippersRed p-2 rounded mb-6"
      >
        {collapsed ? '>' : '<'}
      </button>

      {/* Logo */}
      {!collapsed && (
        <img
          src="https://cdn.nba.com/logos/nba/1610612746/primary/L/logo.svg"
          alt="LA Clippers Logo"
          className="w-24 h-auto mb-8"
        />
      )}

      {/* Navigation Links */}
      <div className={`${collapsed ? 'space-y-2' : 'space-y-4'} text-center`}>
        <Link to="/" className="block hover:underline">
          {collapsed ? 'H' : 'Home'}
        </Link>
        <Link to="/team" className="block hover:underline">
          {collapsed ? 'T' : 'Team'}
        </Link>
        <Link to="/league" className="block hover:underline">
          {collapsed ? 'L' : 'League'}
        </Link>
      </div>
    </nav>
  );
};

export default Navbar;
