import { useState } from 'react';
import GameCards from './GameCards';
import PlayerTable from './PlayerTable';

const League = () => {
  const [team, setTeam] = useState('Atlanta Hawks');
  const [selectedGame, setSelectedGame] = useState(null);

  const handleTeamChange = (e) => setTeam(e.target.value);

  return (
    <div className="bg-clippersWhite text-clippersBlue min-h-screen p-8">
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-3xl font-bold">NBA League Games</h1>
        <select onChange={handleTeamChange} className="p-2 border rounded">
          {/* Add all 30 teams */}
          <option value="Atlanta Hawks">Atlanta Hawks</option>
          <option value="LA Clippers">LA Clippers</option>
        </select>
      </div>
      {/* <GameCards team={team} setSelectedGame={setSelectedGame} /> */}
      {/* {selectedGame && <PlayerTable data={selectedGame.stats} />} */}
    </div>
  );
};

export default League;
