import { useState } from 'react';
import PlayerTable from './PlayerTable';
import Chart from './Chart';

const Team = () => {
  const [data, setData] = useState('starters'); // 'starters', 'bench', etc.

  const handleDataChange = (e) => setData(e.target.value);

  return (
    <div className="bg-clippersWhite text-clippersBlue min-h-screen p-8">
      <div className="flex justify-between items-center mb-6">
        <h1 className="text-3xl font-bold">LA Clippers Team Stats</h1>
        <select onChange={handleDataChange} className="p-2 border rounded">
          <option value="starters">Starters</option>
          <option value="bench">Bench</option>
        </select>
      </div>
      {/* <PlayerTable data={data} /> */}
      {/* <Chart data={data} /> */}
    </div>
  );
};

export default Team;
