import React, { useState } from "react";

const players = [
  {
    name: "Kawhi Leonard",
    image: "https://cdn.nba.com/headshots/nba/latest/1040x760/202695.png",
  },
  {
    name: "Jame Harden",
    image: "https://cdn.nba.com/headshots/nba/latest/1040x760/201935.png",
  },
  {
    name: "Mo Bamba",
    image: "https://cdn.nba.com/headshots/nba/latest/1040x760/1628964.png",
  },
  // Add more players as needed.
];

const Team = () => {
  const [selectedPlayer, setSelectedPlayer] = useState(players[0]);

  return (
    <div className="p-8">
      <h1 className="text-2xl font-bold mb-4">LA Clippers Team</h1>

      {/* Dropdown */}
      <div className="mb-6">
        <label htmlFor="player-select" className="block mb-2 text-lg font-medium">
          Select a Player:
        </label>
        <select
          id="player-select"
          className="border p-2 rounded-md w-full max-w-md"
          value={selectedPlayer.name}
          onChange={(e) =>
            setSelectedPlayer(players.find((p) => p.name === e.target.value))
          }
        >
          {players.map((player) => (
            <option key={player.name} value={player.name}>
              {player.name}
            </option>
          ))}
        </select>
      </div>

      {/* Player Image */}
      <div className="flex flex-col items-center">
        <h2 className="text-xl font-semibold mb-4">{selectedPlayer.name}</h2>
        <img
          src={selectedPlayer.image}
          alt={selectedPlayer.name}
          className="w-48 h-48 object-cover rounded-full shadow-lg"
        />
      </div>
    </div>
  );
};

export default Team;
