import React, { useState, useEffect } from "react";
import ScheduleNavbar from "./ScheduleNavbar";

const players = [
  { name: "James Harden", image: "https://cdn.nba.com/headshots/nba/latest/1040x760/201935.png", position: "Guard", athlete_staff: "athlete", current_season_stats: {
      games_played: 15,
      points_per_game: 24.5,
      rebounds_per_game: 6.8,
      assists_per_game: 9.2,
      steals_per_game: 1.3,
      blocks_per_game: 0.5,
      field_goal_percentage: 45.2,
      three_point_percentage: 38.7,
      free_throw_percentage: 87.5,
      turnovers_per_game: 3.1,
    }
  },
  { name: "Kawhi Leonard", image: "https://cdn.nba.com/headshots/nba/latest/1040x760/202695.png", position: "Forward", athlete_staff: "athlete" },
  { name: "Mo Bamba", image: "https://cdn.nba.com/headshots/nba/latest/1040x760/1628965.png", position: "Center", athlete_staff: "athlete" },
  // Add more players...
];

const schedule = [
  { date: "Nov 21", opponent: "Lakers", time: "7:00 PM" },
  { date: "Nov 23", opponent: "Warriors", time: "7:30 PM" },
  { date: "Nov 25", opponent: "Celtics", time: "6:00 PM" },
  { date: "Nov 28", opponent: "Heat", time: "5:30 PM" },
  { date: "Dec 1", opponent: "Nuggets", time: "8:00 PM" },
];

const Team = () => {
  const [filter, setFilter] = useState("athlete");
  const [filteredPlayers, setFilteredPlayers] = useState([]);
  const [selectedPlayer, setSelectedPlayer] = useState(null);
  const [isSidebarOpen, setIsSidebarOpen] = useState(false);

  // Update filtered players whenever the filter changes
  useEffect(() => {
    const updatedFilteredPlayers = players.filter(
      (player) => player.athlete_staff === filter
    );
    setFilteredPlayers(updatedFilteredPlayers);

    // Set the default selected player to the first player in the updated list
    setSelectedPlayer(updatedFilteredPlayers[0] || null);
  }, [filter]);

  const toggleSidebar = () => {
    setIsSidebarOpen(!isSidebarOpen);
  };

  return (
    <div className="relative p-8">
      {/* Transparent Schedule Button */}
      <button
        onClick={toggleSidebar}
        className="absolute top-4 right-4 text-clippersBlue bg-transparent border border-clippersBlue px-4 py-2 rounded hover:bg-clippersBlue hover:text-white transition duration-200"
      >
        Schedule
      </button>

      <h1 className="text-2xl font-bold mb-4">LA Clippers Team</h1>

      {/* Athlete/Staff Filter */}
      <div className="mb-6">
        <label htmlFor="filter-select" className="block mb-2 text-lg font-medium">
          Filter by:
        </label>
        <select
          id="filter-select"
          className="border p-2 rounded-md w-full max-w-md"
          value={filter}
          onChange={(e) => setFilter(e.target.value)}
        >
          <option value="athlete">Athletes</option>
          <option value="staff">Staff</option>
        </select>
      </div>

      {/* Dropdown for Filtered Players */}
      <div className="mb-6">
        <label htmlFor="player-select" className="block mb-2 text-lg font-medium">
          Select a Player:
        </label>
        <select
          id="player-select"
          className="border p-2 rounded-md w-full max-w-md"
          value={selectedPlayer?.name || ""}
          onChange={(e) =>
            setSelectedPlayer(
              filteredPlayers.find((p) => p.name === e.target.value)
            )
          }
        >
          {filteredPlayers.map((player) => (
            <option key={player.name} value={player.name}>
              {player.name} - {player.position}
            </option>
          ))}
        </select>
      </div>

      {/* Player/Staff Image and Details */}
      {selectedPlayer && (
        <div className="flex flex-col items-center">
          <h2 className="text-xl font-semibold mb-4">{selectedPlayer.name}</h2>
          <p className="text-lg text-gray-600 mb-4">{selectedPlayer.position}</p>
          <img
            src={selectedPlayer.image}
            alt={selectedPlayer.name}
            className="w-48 h-48 object-cover rounded-full shadow-lg"
          />
        </div>
      )}

      {/* Player Statistics Table */}
      {selectedPlayer && selectedPlayer.current_season_stats && (
        <div className="mt-8 w-full max-w-2xl mx-auto">
          <h3 className="text-lg font-semibold mb-4">Season Statistics</h3>
          <table className="min-w-full bg-white border border-gray-200">
            <thead>
              <tr>
                <th className="py-2 px-4 border-b">Games Played</th>
                <th className="py-2 px-4 border-b">PPG</th>
                <th className="py-2 px-4 border-b">RPG</th>
                <th className="py-2 px-4 border-b">APG</th>
                <th className="py-2 px-4 border-b">SPG</th>
                <th className="py-2 px-4 border-b">BPG</th>
                <th className="py-2 px-4 border-b">FG%</th>
                <th className="py-2 px-4 border-b">3P%</th>
                <th className="py-2 px-4 border-b">FT%</th>
                <th className="py-2 px-4 border-b">TO</th>
              </tr>
            </thead>
            <tbody>
              <tr>
                <td className="py-2 px-4 border-b">
                  {selectedPlayer.current_season_stats.games_played}
                </td>
                <td className="py-2 px-4 border-b">
                  {selectedPlayer.current_season_stats.points_per_game}
                </td>
                <td className="py-2 px-4 border-b">
                  {selectedPlayer.current_season_stats.rebounds_per_game}
                </td>
                <td className="py-2 px-4 border-b">
                  {selectedPlayer.current_season_stats.assists_per_game}
                </td>
                <td className="py-2 px-4 border-b">
                  {selectedPlayer.current_season_stats.steals_per_game}
                </td>
                <td className="py-2 px-4 border-b">
                  {selectedPlayer.current_season_stats.blocks_per_game}
                </td>
                <td className="py-2 px-4 border-b">
                  {selectedPlayer.current_season_stats.field_goal_percentage}%
                </td>
                <td className="py-2 px-4 border-b">
                  {selectedPlayer.current_season_stats.three_point_percentage}%
                </td>
                <td className="py-2 px-4 border-b">
                  {selectedPlayer.current_season_stats.free_throw_percentage}%
                </td>
                <td className="py-2 px-4 border-b">
                  {selectedPlayer.current_season_stats.turnovers_per_game}
                </td>
              </tr>
            </tbody>
          </table>
        </div>
      )}

      {/* Schedule Sidebar */}
      <ScheduleNavbar
        isOpen={isSidebarOpen}
        toggleSidebar={toggleSidebar}
        schedule={schedule}
      />
    </div>
  );
};

export default Team;
