  const [team, setTeam] = useState('Atlanta United FC');
  const [selectedGame, setSelectedGame] = useState(null);

  const handleTeamChange = (e) => setTeam(e.target.value);

  return (
    <div className="bg-atlantaGold text-atlantaBlack min-h-screen">
