import React from "react";

const ScheduleNavbar = ({ isOpen, toggleSidebar, schedule }) => {
  return (
    // <>
    <nav
      className={`${
        isOpen ? '' : ''
      } bg-clippersBlue text-clippersWhite p-4 flex flex-col items-center top-0 right-0 transition-all duration-300`}
    >


      {/* Toggle Button */}

{/* 
      <button
        onClick={() => setCollapsed(!collapsed)}
        className="text-clippersWhite bg-clippersRed p-2 rounded mb-6"
      ></button> */}

      {/* <button
        onClick={toggleSidebar}
        className={`fixed top-4 right-4 z-50 p-2 rounded ${
          isOpen
            ? "bg-clippersRed text-white"
            : "bg-transparent border border-clippersBlue text-clippersBlue"
        } hover:bg-clippersBlue hover:text-white transition duration-200`}
      >
        <svg
          xmlns="http://www.w3.org/2000/svg"
          fill="none"
          viewBox="0 0 24 24"
          strokeWidth={2}
          stroke="currentColor"
          className="w-6 h-6"
        >
          <path
            strokeLinecap="round"
            strokeLinejoin="round"
            d="M4 6h16M4 12h16M4 18h16"
          />
        </svg>
      </button> */}

      {/* Collapse/Expand Button */}
      <button
        // onClick={() => setCollapsed(!collapsed)}
        onClick={toggleSidebar}

        className="text-clippersWhite bg-clippersRed p-2 rounded mb-6"
      >
        {isOpen ? (
          <svg
            xmlns="http://www.w3.org/2000/svg"
            fill="none"
            viewBox="0 0 24 24"
            strokeWidth={2}
            stroke="currentColor"
            className="w-6 h-6"
          >
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              d="M4 6h16M4 12h16M4 18h16"
            />
          </svg>
        ) : (
          <svg
            xmlns="http://www.w3.org/2000/svg"
            fill="none"
            viewBox="0 0 24 24"
            strokeWidth={2}
            stroke="currentColor"
            className="w-6 h-6"
          >
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              d="M4 6h16M4 12h16M4 18h16"
            />
          </svg>
        )}
      </button>



      {/* Schedule Navbar */}
      <div
        className={`fixed top-0 right-0 h-full w-64 bg-clippersBlue text-white p-4 transition-transform duration-300 ${
          isOpen ? "transform translate-x-0" : "transform translate-x-full"
        // isOpen ? "" : ""

        }`}
      >
        {/* Close Button
        <button
          onClick={toggleSidebar}
          className="absolute top-4 left-4 bg-white text-clippersBlue p-2 rounded hover:bg-clippersRed hover:text-white transition duration-200"
        >
          <svg
            xmlns="http://www.w3.org/2000/svg"
            fill="none"
            viewBox="0 0 24 24"
            strokeWidth={2}
            stroke="currentColor"
            className="w-6 h-6"
          >
            <path
              strokeLinecap="round"
              strokeLinejoin="round"
              d="M6 18L18 6M6 6l12 12"
            />
          </svg>
        </button> */}

        {/* Schedule Content */}
        <h2 className="text-xl font-bold mb-4 mt-12">Season Schedule</h2>
        <ul>
          {schedule.map((game, index) => (
            <li key={index} className="mb-2">
              {game.date} - {game.opponent} at {game.time}
            </li>
          ))}
        </ul>
      </div>
    </nav>
  );
};

export default ScheduleNavbar;
